INSERT INTO `ciganadigital`.`tb_nomes`( `nome`, `significado`) VALUES( 'Nelita','Nelita: Apelido carinhoso para Helena.');
INSERT INTO `ciganadigital`.`tb_nomes`( `nome`, `significado`) VALUES( 'Loreta','Loreta: Latim, bosquezinho de loureiros. Diminutivo de Laura. Masculino: Loreto. Temperamental, ora é simpática e agradável, ora intolerante e sisuda. Por isso, enfrenta dificuldades nos relacionamentos, pois nem todos entendem suas mudanças bruscas de humor. Conquista facilmente o parceiro.');
