INSERT INTO `ciganadigital`.`tb_nomes`( `nome`, `significado`) VALUES( 'Hilde','Hilde: Significa donzela da batalha e indica uma pessoa muito voluntariosa, que em geral só faz o que quer. Praticamente indiferente à glória e à riqueza material, ela parece distante e distraída, mas é atenta a tudo o que acontece e tem idéias muitas claras.');
