INSERT INTO `ciganadigital`.`tb_nomes`( `nome`, `significado`) VALUES( 'Dyane','Dyane: Significa “divina”.É uma das versões inglesas de Diana, que vem do latim Diana, a partir da palavra dius, que quer dizer literalmente “divina”.Na mitologia romana Diana é a deusa da lua, da caça e das florestas, por ter como uma forma arcaica a versão Diviana, que quer dizer “da natureza de Divia”, onde Divia é a deusa da Lua e significa “a que ilumina”.');
