INSERT INTO `ciganadigital`.`tb_nomes`( `nome`, `significado`) VALUES( 'Ignês','Ignês: Significa “casta”, “pura”.É uma variante gráfica de Inês, a partir de Agnes, que vem do grego hagnes, que significa literalmente “pura, casta”.Inês de Castro foi a primeira esposa de D. Pedro I, que foi coroada rainha depois de morta e deu origem a expressão “Agora, Inês é morta”, que significa “agora é tarde”.');
