INSERT INTO `ciganadigital`.`tb_nomes`( `nome`, `significado`) VALUES( 'Rozália','Rozália: Variante gráfica do nome Rosália.Vem do latim Rosalia nome de uma festa antiga, romana, equivalente ao atual Dia de finados. A tradução é "Dia das Rosas". Nesse dia as pessoas depositavam rosas sobre os túmulos dos entes queridos.Pode significar "a que é como a rosa". É uma derivação do nome Rosa.');
